import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { auth } from "../actions/user";
import { setLocalStorageItem , getLocalStorageItem} from "../utils/globals";

const Header = ( props ) => {
    // useEffect(() => {
    //     props.auth();
    // }, [props.auth]);

    useEffect(() => {
        console.log(props.userDetail)
        let userDetail = getLocalStorageItem('userDetail');
        if(userDetail){
            userDetail.user = props.userDetail;
        }
        setLocalStorageItem('userDetail' , userDetail)
    }, [props.userDetail])
    return(
    <div>
        This is Header
        {/* <Link to="/" className="link">Home</Link>
        <Link to="/about" className="link">About</Link>
        <Link to="/contact" className="link">Contact</Link>
        { loggedIn && <Link to="/secret" className="link">Secret</Link> } */}
    </div>
    )

}

const mapStateToProps = ( state ) => ( {
    loggedIn: state.loggedIn,
    userDetail: state.user.userDetail
} );

const mapDispatchToProps = {
    auth,
};

export default connect( mapStateToProps, mapDispatchToProps )( Header );
