import React from "react";
import { connect } from "react-redux";
import { fetchData } from "../../actions/home";
import { checkLogin } from "../../utils/globals";
import TextEditor from "../../components/widgets/TextEditor";
import headerImg from "../../scss/images/features/bg-1.svg";
import ats from "../../scss/images/ats/svg"
class Home extends React.Component {
    constructor(props){
        super();
        this.logout = this.logout.bind(this);
    }
    componentDidMount( ) {
        if ( this.props.users && this.props.users.length <= 0 ) {
            this.props.fetchData();
        }
    }

    logout(){
        if(typeof window !== 'undefined'){
            window.localStorage.removeItem('userDetail');
            window.location.href = '/';
        }
    }
    render( ) {
        const userDetails = checkLogin();
        const { users } = this.props;

        const getMyAccount = (userDetails) => {
            return (
                <div>
                    {/* <TextEditor /> */}
                    <a href="/my-account">My Account ({userDetails.user.first_name} {userDetails.user.last_name}) </a>
                    <span classNameName="link" onClick={() => this.logout()}> Logout</span>
                </div>
            )
        }

        return (
            <div>
                 <section className="bg-home align-items-center" id="home">
                    <div className="container">
                        <div className="row align-items-center">
                            <div className="col-lg-6">
                                <div className="home-contact mt-4">


                                    <h1 className="home-title mt-3 mb-0">Lorem ipsum dolor</h1>
                                    <h1 className="home-title">Lorem ipsum dolor <span className="element text-primary" data-elements="sit a egestas risus."></span></h1>

                                    <p className="text-muted mt-4">
                                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Dui libero sagittis, sit a egestas risus.
                                    </p>

                                    <div className="mt-4 pt-3">
                                        <a href="" className="btn btn-primary ">Get started for free</a>
                                        <a href="" className="btn btn-outline-primary">Create your resume for free</a>
                                    </div>


                                </div>
                            </div>

                            <div className="col-lg-6">

                                <img src={headerImg} className="img-fluid" alt="" />
                            </div>
                        </div>
                    </div>
                </section>

                <section className="section pb-5">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="text-center">
                                    <h2 className="title-heading"><mark></mark>Benefits of using our product</h2>
                                    <p className="title-desc text-muted mt-2">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Dui libero sagittis, sit a egestas risus.</p>
                                </div>
                            </div>
                        </div>

                        <div className="row mt-5 pt-4">
                            <div className="col-md-6">
                                <div className="nav nav-pills" id="v-pills-tab" role="tablist" aria-orientation="horizontal">
                                    <a className="mt-3 mr-2 text-center features-box nav-link active" id="v-pills-home-tab" data-toggle="pill" href="#ATS" role="tab" aria-controls="ATS" aria-selected="true"><img className="d-block mt-3" src={ats} alt="work-img" /> ATS friendly templates</a>
                                    <a className="mt-3 mr-2 text-center features-box nav-link" id="v-pills-profile-tab" data-toggle="pill" href="#Instant" role="tab" aria-controls="Instant" aria-selected="false"><img  className="d-block mt-3" src="../../scss/images/instant.svg" alt="work-img" /> Instant Customization</a>
                                    <a className="mt-3 mr-2 text-center features-box nav-link" id="v-pills-messages-tab" data-toggle="pill" href="#Easy" role="tab" aria-controls="Easy" aria-selected="false"><img  className="d-block mt-3" src="../../scss/images/easy.svg" alt="work-img" /> Easy to update and quick to access </a>
                                    <a className="mt-3 mr-2 text-center features-box nav-link" id="v-pills-settings-tab" data-toggle="pill" href="#Professional" role="tab" aria-controls="Professional" aria-selected="false"><img  className="d-block mt-3" src="../../scss/images/professional.svg" alt="work-img" /> Professional resume templates</a>
                                    <a className="mt-3 mr-2 text-center features-box nav-link" id="v-pills-messages-tab" data-toggle="pill" href="#Create" role="tab" aria-controls="Create" aria-selected="false"><img  className="d-block mt-3" src="../../scss/images/create.svg" alt="work-img" /> Create resume in minutes</a>
                                    <a className="mt-3 mr-2 text-center features-box nav-link" id="v-pills-settings-tab" data-toggle="pill" href="#Ready" role="tab" aria-controls="Ready" aria-selected="false"><img className="d-block mt-3" src="../../scss/images/ready.svg" alt="work-img" /> Ready to use templates</a>
                                </div>
                                <a href="" className="btn btn-primary mt-5 d-block ">Start building your resume</a>
                            </div>
                            <div className="col-lg-6 tab-content" id="v-pills-tabContent">
                                    <div className="tab-pane fade show active text-center" id="ATS" role="tabpanel" aria-labelledby="v-pills-home-tab"><img className="d-block mt-3" src="../../scss/images/ats1.svg" alt="work-img" /><p className="mt-3">Benefits of using our product</p></div>
